    private void tblProductMouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_tblProductMouseClicked
        int selection = tblProduct.getSelectedRow();
        
        txtProductID.setText(tblProduct.getValueAt(selection, 0) + "");
        txtProduct.setText(tblProduct.getValueAt(selection, 1) + "");
        txtPrice.setText(tblProduct.getValueAt(selection, 2) + "");
        txtQuantity.setText(tblProduct.getValueAt(selection, 3) + "");
    }//GEN-LAST:event_tblProductMouseClicked

