    private void save() {
        
        int id = (int) (Math.random() * 10000);
        
        double price = Double.parseDouble(txtPrice.getText());
        
        double quantity = Double.parseDouble(txtQuantity.getText());
        
        try {
            
            Map<String, Object> datos = new HashMap<>();
            
            datos.put("Cantidad", quantity);
            datos.put("Precio", price);
            datos.put("Producto", txtProduct.getText());
            
            itemInStorage.saveItem("Bodega", String.valueOf(id), datos);
            
            JOptionPane.showMessageDialog(null, "Guardado con éxito");
            
            clearForm();
            
        } catch (HeadlessException e) {
             
            System.err.println("Error: " + e.getMessage());
            
            JOptionPane.showMessageDialog(null, "Error al guardar");
            
        } finally {
            
            itemInStorage.loadItemTable(tblProduct);
            
        }
    }

    private void delete() {
        
        String idDoc = txtProductID.getText();
        
        try {
             
            itemInStorage.deleteItem("Bodega", idDoc);
            
            JOptionPane.showMessageDialog(null, "Eliminado con éxito");
            
            clearForm();
            
        } catch (HeadlessException e) {
             
            System.err.println("Error: " + e.getMessage());
            
            JOptionPane.showMessageDialog(null, "Error al eliminar");
            
        } finally{
            
            itemInStorage.loadItemTable(tblProduct);
            
        }
    }

    private void update() {
        
        String idDoc = txtProductID.getText();
        
        double price = Double.parseDouble(txtPrice.getText());
        
        double quantity = Double.parseDouble(txtQuantity.getText());
        
        try {
            
            Map<String, Object> datos = new HashMap<>();
            
            datos.put("Producto", txtProduct);
            datos.put("Precio", price);
            datos.put("Cantidad", quantity);
            
            itemInStorage.updateItem("Bodega", idDoc, datos);
            
            JOptionPane.showMessageDialog(null, "Actulizado con éxito");
            
            clearForm();
            
        } catch (HeadlessException e) {
             
            System.err.println("Error: " + e.getMessage());
            
            JOptionPane.showMessageDialog(null, "Error al actualizar");
            
        } finally {
            
            itemInStorage.loadItemTable(tblProduct);
            
        }
    }
    
    void clearForm(){
        
        txtProduct.setText("");
        txtPrice.setText("");
        txtQuantity.setText("");
        txtProductID.setText("");
  
    }
